"use strict";
class SegmentService {

//Segment_Definition__c list get set
get segments(){ return this._segments; }   


set segments(val){ this._segments = val; }

constructor(){
    this.segments = this.createSegDefMock();
    
}

createSegDefMock(){
    var segDefList = [];
    
    var CompSegment = new SegmentService.SegmentDefinitionWrapper();
    CompSegment.loadData('Company__c','Company__c',1,'Company__c','Company__c',null,null);
    var GLSegment   = new SegmentService.SegmentDefinitionWrapper();
    GLSegment.loadData('GL_Account__c',1,'GL_Account__c','GL_Account__c','GL_Account__c','GL_Account_reference__c','Default_GL_Account__c');
    
    segDefList.push(CompSegment);
    segDefList.push(GLSegment);
    debugger;
    return segDefList;
}

}
 SegmentService.SegmentDefinitionWrapper = class {
    
    //Initialize null?Good practice?
    constructor(){
        this.Object_API_Name__c = null;
        this.Segment_Number__c = null;
        this.Accounting_Rule_Default__c = null;
        this.Name = null;
        this.Accounting_Rule_Reference_Value__c = null;
        this.Accounting_Entry_Field__c = null;
        this.Accounting_Rule_Segment__c = null;      
    }
    loadData(Name,ObjName,SegNum,AElFieldVal,ARuleSegment,ARRuleRef,ARuleDefault){
        this.Name = Name;       
        this.Object_API_Name__c = ObjName;
        this.Segment_Number__c = SegNum;
        this.Accounting_Entry_Field__c = AElFieldVal;
        this.Accounting_Rule_Reference_Value__c = ARRuleRef;
        this.Accounting_Rule_Default__c = ARuleDefault;       
        this.Accounting_Rule_Segment__c = ARuleSegment;      
    }
}

    module.exports = {
        SegmentService: SegmentService,
    };


